Source.BuildSpec:
{
  "version": "0.2",
  "phases": {
    "install": {
      "commands": [
        "npm install"
      ]
    },
    "build": {
      "commands": [
        "npm run build",
        "npm run cdk synth -- -o dist"
      ]
    }
  },
  "artifacts": {
    "secondary-artifacts": {
      "BuildOutput": {
        "base-directory": "$CODEBUILD_SRC_DIR/dist",
        "files": [
          " **/*"
        ]
      },
      "LambdaBuildOutput": {
        "base-directory": "$CODEBUILD_SRC_DIR/lambda-test",
        "files": [
          " **/*"
        ]
      }
    }
  }
}
